function convertTextToJson(e){const r=(e+="Schau auf meinen Blog, um mehr zu erfahren. Link in der Bio.").trim().split(/(?<=[.!?])/).filter((e=>e.trim().length>0)),o=require("fs"),t=o.readdirSync("images").filter((e=>e.endsWith(".jpg")||e.endsWith(".png"))).map((e=>`src/images/${e}`)),n=o.readdirSync("../public/src/music");if(0===n.length)throw new Error("No audio files found in the music directory.");let s=n[Math.floor(Math.random()*n.length)],i=[];if(!s.endsWith(".mp3"))throw new Error("No valid audio files found in the music directory.");i.push(`src/music/${s}`);const c={audioUrl:i,images:t,words:[]};let l=0;return r.forEach((e=>{e.trim().split(/\s+/).filter((e=>e.length>0)).forEach(((e,r)=>{const o=.08*e.length+.2,t=parseFloat(l.toFixed(2));l+=o;const n=parseFloat(l.toFixed(2));l+=.02,c.words.push({word:e,start:t,end:n,confidence:(.1*Math.random()+.9).toFixed(7),punctuated_word:e})})),l+=.3})),JSON.stringify(c,null,2)}function processTextFile(e){return convertTextToJson(e)}if("undefined"!=typeof require){const e=require("fs");if(process.argv.length>=4){const r=process.argv[2],o=process.argv[3];try{const t=convertTextToJson(e.readFileSync(r,"utf8"));e.writeFileSync(o,t),console.log(`Successfully converted ${r} to ${o}`)}catch(e){console.error("Error:",e.message)}}else console.log("Usage: node script.js input.txt output.json")}